import { Injectable } from "@angular/core";
import { Subject } from 'rxjs';

import { Ingrediant } from "../shared/Ingrediant.Model";
import { Recipe } from "./recipe.Model";
import { ShoppingListService } from './../shopping-list/shoping-list.service';
import { Store } from "@ngrx/store";

import * as ShoppingListActions from '../shopping-list/store/shopping-list.actions'
import * as fromShoppingList from "../shopping-list/store/shopping-list.reducers";

@Injectable()
export class RecipeService{

    // private recipes:Recipe[] = [
    //     new Recipe("Big Fat Burger", "Burger With Tomato","data:image/jpeg;base64,", 
    //     [
    //         new Ingrediant("Tomato", 2),
    //         new Ingrediant("Burger", 2)
    //     ]),
    //     new Recipe("Pasta", "Delicius Creamed Macrona","data:image/jpeg;base64,", 
    //     [
    //         new Ingrediant("Creama", 1),
    //         new Ingrediant("spaggti", 7)
    //     ]),
    //   ];

    private recipes:Recipe[] = [];

      recipesChanged = new Subject<Recipe[]>();
      constructor(private slService:ShoppingListService,
                  private store: Store<{shoppingList: fromShoppingList.State}>){

      }

    public setRecipes(recipes: Recipe[]){
      this.recipes = recipes;
      this.recipesChanged.next(this.recipes.slice());
    }

    public getRecipes(){
        return this.recipes.slice();
    }

    getRecipeOf(index: number): Recipe {
        return this.recipes[index];
      }

    addToShoppingList(recipe: Recipe) {
      this.store.dispatch(new ShoppingListActions.AddIngridants(recipe.ingrediants))
      // this.slService.AddIngrediants(recipe.ingrediants);
    }

    addRecipe(recipe: Recipe){
      this.recipes.push(recipe);
      this.recipesChanged.next(this.recipes.slice());
    }

    updateRecipe(index: number, newRecipe: Recipe){
      this.recipes[index] = newRecipe;
      this.recipesChanged.next(this.recipes.slice());
    }

    deleteRecipe(index: number){
      this.recipes.splice(index, 1);
      this.recipesChanged.next(this.recipes.slice());
    }
}